import React from "react";
import "./button.css";
import reload from "./reload.pngreload.png";

const Button = ({ type, name, onClick, disabled }) => {
  const handleClick = () => {
    onClick();
    const reloadIcon = document.getElementById("reload-icon");
    reloadIcon.classList.add("rotate-icon");
    setTimeout(() => {
      reloadIcon.classList.remove("rotate-icon");
    }, 2000);
  };

  return (
    <button
      className={type === "SORT" ? "button sort" : "button newArray"}
      disabled={disabled}
      onClick={handleClick}
    >
      {name}
      <img src={reload} className="reload-icon" alt="Reload Icon" />
    </button>
  );
};

export default Button;
