import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import "./Sort.css";
import Button from '../assets/Button'
import Slider from '../assets/Slider'
import HeroImage2 from "./HeroImage2"
import InsertionSort from ""

const ARRAYSIZE = 50

const BucketSort = () => {
  const [primaryArray, setPrimaryArray] = useState([])
  const [algorithm, setAlgorithm] = useState('bucketSort')
  const [animationSpeed, setAnimationSpeed] = useState(50)
  const [disableOptions, setDisableOptions] = useState(false)

  const randomizeArray = () => {
    for (let i = 0; i < primaryArray.length; i++) {
      let bar = document.getElementById(i).style
      bar.backgroundColor = 'rgb(35, 17, 95)'
    }
    let array = []
    for (let i = 0; i < ARRAYSIZE; i++) {
      array.push(randomVals(20, 400))
    }

    setPrimaryArray(array)
  }

  const randomVals = (min, max) => {
    let randomVal = Math.floor(Math.random() * (max - min + 1) + min)
    return randomVal
  }

  useEffect(() => {
    randomizeArray()
  }, [])

  const sleep = (milliSeconds) => {
    return new Promise((resolve) => setTimeout(resolve, milliSeconds))
  }

  const finishedAnimation = async () => {
    for (let i = 0; i < primaryArray.length; i++) {
      let bar = document.getElementById(i).style
      bar.backgroundColor = 'orange'

      await sleep(animationSpeed)
    }
    setDisableOptions(false)
  }

  const handleSorting = () => {
    setDisableOptions(true)
    switch (algorithm) {
      case 'BucketSort':
        BucketSort()
        break
      default:
        break
    }
  }

  const BucketSort = async () => {
    const totalSlots = 10;
  let buckets = [...Array(totalSlots)].map(() => []);
  let animationArr = [];
  let id = 0;
  for (const item of arr) {
    const location = Math.floor(totalSlots * item.height);
    animationArr.push({
      id: id,
      height: item.height,
      isShown: true,
      location: location,
      isDistributing: true,
    });
    id++;
    buckets[location].push(item);
  }

  // Sort all arrays in buckets
  for (let i = 0; i < totalSlots; i++) {
    // Signify start of sort
    animationArr.push({
      isSort: true,
      location: i,
    });

    // Execute insertion sort animations
    let insertionSortAnimation = InsertionSort(buckets[i]).map((arr) => [...arr, i]);
    animationArr = animationArr.concat(insertionSortAnimation);

    // Signify end of sort
    animationArr.push({
      isSort: false,
      location: i,
    });

  }

  // Restore element back into array
  id = 0;
  let location = 0;
  for (const array of buckets) {
    for (const item of array) {
      animationArr.push({
        id: id,
        height: item.height,
        isShown: true,
        location: location,
        isDistributing: false,
        isSwap: false,
      });
      id++;
    }
    location++;
  }
    finishedAnimation()
    
  }

    return (
      <div>
        <Navbar/>
        <HeroImage2 heading="Bucket Sort" text="Time Complexity : O(n)"/>
        <div className='container3'>
          <Button
            type='NEWARRAY'
            name='New Array'
            onClick={randomizeArray}
            disabled={disableOptions}
          />
          <Slider
            onChange={(e) => setAnimationSpeed(e.target.value)}
            disabled={disableOptions}
          />
          <Button
            onClick={handleSorting}
            type='SORT'
            name='Sort'
            disabled={disableOptions}
          />
        </div>
        <div className='sortingBars'>
          {primaryArray &&
            primaryArray.map((val, key) => {
              return (
                <div
                  className='bars'
                  id={key}
                  key={key}
                  style={{ height: val }}
                ></div>
              )
            })}
        </div>
      </div>
  )
}

export default BucketSort;
