import React, { useState } from "react";
import "./button.css";
import reload from "./reload.png.jpg";

const Button = ({ type, name, onClick, disabled }) => {

  const [rotate, setRotate] = useState(false); // State to manage rotation

  const handleClick = () => {
    onClick();
    setRotate(true); // Start rotation
    setTimeout(() => {
      setRotate(false); // Stop rotation after 2 seconds
    }, 2000);
  };

  return (
    <button
      className={type === "SORT" ? "button sort" : "button newArray"}
      disabled={disabled}
      onClick={handleClick}
    >
      {name}
      {rotate && (
        <img src={reload} className="reload-icon" alt="Reload Icon" />
      )}
    </button>
  );
};

export default Button;
