    for(let i = 1; parseInt(max / i) > 0; i *= 10){
      countingSort(arr, ARRAYSIZE, i);
    }
    finishedAnimation()
  }
    return (
      <div>
        <Navbar/>
        <HeroImage2 heading="Limited Quicksort" text="Time Complexity : O(n)"/>
        <div className='container3'>
          <Button
            type='NEWARRAY'
            name='New Array'
            onClick={randomizeArray}
            disabled={disableOptions}
          />
          <Slider
            onChange={(e) => setAnimationSpeed(e.target.value)}
            disabled={disableOptions}
          />
          <Button
            onClick={handleSorting}
            type='SORT'
            name='Sort'
            disabled={disableOptions}
          />
        </div>
        <div className='sortingBars'>
          {primaryArray &&
            primaryArray.map((val, key) => {
              return (
                <div
                  className='bars'
                  id={key}
                  key={key}
                  style={{ height: val }}
                ></div>
              )
            })}
        </div>
      </div>
  )
}

export default LimitedQuicksort;
