import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import "./Sort.css";
import Button from '../assets/Button'
import Slider from '../assets/Slider'
import HeroImage2 from "./HeroImage2"
import InsertionSort from "./InsertionSort"

const primaryArrayAYSIZE = 50

const BucketSort = () => {
  const [primaryprimaryArrayay, setPrimaryprimaryArrayay] = useState([])
  const [algorithm, setAlgorithm] = useState('bucketSort')
  const [animationSpeed, setAnimationSpeed] = useState(50)
  const [disableOptions, setDisableOptions] = useState(false)

  const randomizeprimaryArrayay = () => {
    for (let i = 0; i < primaryprimaryArrayay.length; i++) {
      let bar = document.getElementById(i).style
      bar.backgroundColor = 'rgb(35, 17, 95)'
    }
    let primaryArrayay = []
    for (let i = 0; i < primaryArrayAYSIZE; i++) {
      primaryArrayay.push(randomVals(20, 400))
    }

    setPrimaryprimaryArrayay(primaryArrayay)
  }

  const randomVals = (min, max) => {
    let randomVal = Math.floor(Math.random() * (max - min + 1) + min)
    return randomVal
  }

  useEffect(() => {
    randomizeprimaryArrayay()
  }, [])

  const sleep = (milliSeconds) => {
    return new Promise((resolve) => setTimeout(resolve, milliSeconds))
  }

  const finishedAnimation = async () => {
    for (let i = 0; i < primaryprimaryArrayay.length; i++) {
      let bar = document.getElementById(i).style
      bar.backgroundColor = 'orange'

      await sleep(animationSpeed)
    }
    setDisableOptions(false)
  }

  const handleSorting = () => {
    setDisableOptions(true)
    switch (algorithm) {
      case 'BucketSort':
        BucketSort()
        break
      default:
        break
    }
  }

  const BucketSort = primaryArray => {
    
    if (primaryArray.length === 0) {
    }
    let i,
    minValue = primaryArray[0],
    maxValue = primaryArray[0],
    bucketSize = primaryArray.length;
    primaryArray.forEach(function (currentVal) {
       if (currentVal < minValue) {
          minValue = currentVal;
       } else if (currentVal > maxValue) {
          maxValue = currentVal;
       }
    })
    let bucketCount = Math.floor((maxValue - minValue) / bucketSize) + 1;
    let allBuckets = new primaryArray(bucketCount);
    for (i = 0; i < allBuckets.length; i++) {
       allBuckets[i] = [];
    }
    primaryArray.forEach(function (currentVal) {
       allBuckets[Math.floor((currentVal - minValue) / bucketSize)].push(currentVal);
    });
    primaryArray.length = 0;
    allBuckets.forEach(function(bucket) {
       insertion(bucket);
       bucket.forEach(function (element) {
          primaryArray.push(element)
       });
    });
    finishedAnimation()
    // return primaryArray;
  }

  const insertion = primaryArray => {
    let length = primaryArray.length;
    let i, j;
    for(i = 1; i < length; i++) {
       let temp = primaryArray[i];
       for(j = i - 1; j >= 0 && primaryArray[j] > temp; j--) {
        primaryArray[j+1] = primaryArray[j];
       }
       primaryArray[j+1] = temp;
    }
    // return primaryArray;
  };

    return (
      <div>
        <Navbar/>
        <HeroImage2 heading="Bucket Sort" text="Time Complexity : O(n)"/>
        <div className='container3'>
          <Button
            type='NEW ARRA'
            name='New primaryArrayay'
            onClick={randomizeprimaryArrayay}
            disabled={disableOptions}
          />
          <Slider
            onChange={(e) => setAnimationSpeed(e.target.value)}
            disabled={disableOptions}
          />
          <Button
            onClick={handleSorting}
            type='SORT'
            name='Sort'
            disabled={disableOptions}
          />
        </div>
        <div className='sortingBars'>
          {primaryprimaryArrayay &&
            primaryprimaryArrayay.map((val, key) => {
              return (
                <div
                  className='bars'
                  id={key}
                  key={key}
                  style={{ height: val }}
                ></div>
              )
            })}
        </div>
      </div>
  )
}

export default BucketSort;
