import React, { useState } from "react";
import "./slider.css";

const Slider = ({ onChange, disabled }) => {
  const [checked, setChecked] = useState("fast");

  const handleChange = (event) => {
    setChecked(event.target.value);
    onChange(event); // Propagate the change event to the parent component if needed
  };

  return (
    <div onChange={handleChange}>
      <div className="slider-title">
        Select Speed
        <div class="circle-container">
          <input
            type="radio"
            name="speed"
            id="slow"
            value={500}
            disabled={disabled}
          />
          <label for="slow">Slow</label>

          <input
            type="radio"
            name="speed"
            id="medium"
            value={50}
            disabled={disabled}
          />
          <label for="medium">Medium</label>

          <input
            type="radio"
            name="speed"
            id="fast"
            value={5}
            disabled={disabled}
          />
          <label for="fast">Fast</label>

          <div class="circle">
          <span class="selected-item"></span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slider;
