import React, { useEffect } from "react";
import "./slider.css";

const Slider = ({ onChange, disabled }) => {
  useEffect(() => {
    const circles = document.querySelectorAll('.circle');

    circles.forEach(circle => {
      circle.addEventListener('click', () => {
        circles.forEach(c => c.classList.remove('selected'));
        circle.classList.add('selected');
      });
    });
  }, []);                                                                                                                    

  return (
    <div className="circle-container">
      <input type="radio" name="speed" id="slow" value={500} disabled={disabled} />
      <label for="slow" className="circle slow">Slow</label>

      <input type="radio" name="speed" id="medium" value={50} disabled={disabled} />
      <label for="medium" className="circle medium">Medium</label>
      
      <input type="radio" name="speed" id="fast" value={5} disabled={disabled} />
      <label for="fast" className="circle fast">Fast</label>
    </div>
  );
};

export default Slider;
