import React, { useState } from "react";
import "./slider.css";

const Slider = ({ onChange, disabled }) => {
  const [lastChecked, setLastChecked] = useState("fast");

  const handleChange = (event) => {
    setLastChecked(event.target.value);
    onChange(event);
  };

  return (
    <div onChange={handleChange}>
      <ul>
        <li>
          <input
            type="radio"
            name="speed"
            id="slow"
            value="slow"
            checked={lastChecked === "slow"}
            disabled={disabled}
          />
          <label htmlFor="slow">Slow</label>
          <div className={lastChecked === "slow" ? "check slow" : "check"}></div>
        </li>

        <li>
          <input
            type="radio"
            name="speed"
            id="medium"
            value="medium"
            checked={lastChecked === "medium"}
            disabled={disabled}
          />
          <label htmlFor="medium">Medium</label>
          <div className={lastChecked === "medium" ? "check medium" : "check"}></div>
        </li>

        <li>
          <input
            type="radio"
            name="speed"
            id="fast"
            value="fast"
            checked={lastChecked === "fast"}
            disabled={disabled}
          />
          <label htmlFor="fast">Fast</label>
          <div className={lastChecked === "fast" ? "check fast" : "check"}></div>
        </li>
      </ul>
    </div>
  );
};

export default Slider;
