import React, { useState } from "react";
import "./slider.css";

const Slider = ({ onChange, disabled }) => {
  const [activeButton, setActiveButton] = useState("slow");

  const handleRadioChange = (e) => {
    setActiveButton(e.target.id);
    onChange(e)
  };

  return (
    <div onChange={handleRadioChange}>
      <ul>
        <li>
          <input
            type="radio"
            name="speed"
            id="slow"
            value={500}
            disabled={disabled}
            checked={activeButton === "slow"}
          />
          <label htmlFor="slow">Slow</label>
          <div className="check"></div>
        </li>

        <li>
          <input
            type="radio"
            name="speed"
            id="medium"
            value={50}
            disabled={disabled}
            checked={activeButton === "medium"}
          />
          <label htmlFor="medium">Medium</label>
          <div className="check"></div>
        </li>

        <li>
          <input
            type="radio"
            name="speed"
            id="fast"
            value={5}
            disabled={disabled}
            checked={activeButton === "fast"}
          />
          <label htmlFor="fast">Fast</label>
          <div className="check"></div>
        </li>
      </ul>
    </div>
  );
};

export default Slider;
