import React from 'react';
import './button.css';

const Button = ({ type, name, onClick, disabled }) => {
  const handleClick = () => {
    onClick();

    // Start rotation animation when button is clicked
    const reloadIcon = document.getElementById('reload-icon');
    reloadIcon.classList.add('rotate-icon');

    // Stop rotation animation after 2 seconds
    setTimeout(() => {
      reloadIcon.classList.remove('rotate-icon');
    }, 2000);
  };

  return (
    <button
      className={type === 'SORT' ? 'button sort' : 'button newArray'}
      disabled={disabled}
      onClick={handleClick} // Call handleClick instead of onClick directly
    >
      {name}
      {/* Add reload icon */}
      {type === 'NEW_ARRAY' && (
        <span id="reload-icon">&#x21bb;</span>
      )}
    </button>
  );
};

export default Button;
