import React, { useState } from "react";
import "./button.css";
import reloadImage from "./reload.png";

const Button = ({ type, name, onClick, disabled }) => {
  const [showImage, setShowImage] = useState(false);

  const handleClick = () => {
    // Toggle the state to show the image
    setShowImage(true);

    // Call onClick handler when button is clicked
    onClick();

    setTimeout(() => {
      setShowImage(false);
    }, 2000);
  };

  return (
    <button
      className={type === "SORT" ? "button sort" : "button newArray"}
      disabled={disabled}
      onClick={handleClick}
    >
      {showImage ? (
        <img src={reloadImage} alt="Reload" />
      ) : (
        <span>{name}</span>
      )}
    </button>
  );
};

export default Button;
