import React, { useState } from "react";
import "./slider.css";

const Slider = ({ onChange, disabled }) => {
  const [checked, setChecked] = useState("fast");

  const handleChange = (event) => {
    setChecked(event.target.value);
    onChange(event); // Propagate the change event to the parent component if needed
  };

  return (
    <div onChange={handleChange}>
      <div className="slider-title">Select Speed
      <div className="circle"></div>
      </div>
      <div>
        <ul>
          <li>
            <input
              type="radio"
              name="speed"
              id="slow"
              value="slow"
              checked={checked === "slow"}
              disabled={disabled}
            />
            <label htmlFor="slow">Slow</label>
            <div className="check"></div>
          </li>

          <li>
            <input
              type="radio"
              name="speed"
              id="medium"
              value="medium"
              checked={checked === "medium"}
              disabled={disabled}
            />
            <label htmlFor="medium">Medium</label>
            <div className="check"></div>
          </li>

          <li>
            <input
              type="radio"
              name="speed"
              id="fast"
              value="fast"
              checked={checked === "fast"}
              disabled={disabled}
            />
            <label htmlFor="fast">Fast</label>
            <div className="check"></div>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Slider;
